mapper.update:
update member set
	me_pw = #{member.me_pw},
	me_email = #{member.me_email},
	me_phone = #{member.me_phone},
	me_nick = #{member.me_nick}
where me_id = #{member.me_id}

update grade set
	gr_name = #{grade.gr_name},
	gr_discount = #{grade.gr_discount},
	gr_loan_condition = #{grade.gr_loan_condition},
	gr_post_condition = #{grade.gr_post_condition}
where gr_num = #{grade.gr_num}

update member set
	me_fail_count = #{failCount}
where me_id = #{me_id}

update member set
	me_ms_num = #{num}
where me_id = #{me_id}

mapper.select:
select * from book join loan on lo_bo_num = bo_num where lo_me_id = #{user.me_id}
and (bo_title like concat('%', #{cri.search}, '%') or bo_publisher like concat('%', #{cri.search}, '%'))
and bo_title like concat('%', #{cri.search}, '%')
and bo_publisher like concat('%', #{cri.search}, '%')
group by bo_num order by lo_limit asc LIMIT #{cri.pageStart}, #{cri.perPageNum}
