mapper.select:
select post.*, category.*, member.me_nick, (select count(*) from comment where comment.co_po_num = post.po_num) as po_co_count, (select count(*) from heart where heart.he_po_num = post.po_num) as po_totalHeart, (select count(*) from vote where vote.vo_po_num = post.po_num and vote.vo_state = 1) as po_votePost from post join member on po_me_id = me_id join category on po_ca_num = ca_num where
(po_title like concat("%",#{cri.search},"%") or po_content like concat("%",#{cri.search},"%") or po_me_id like concat("%",#{cri.search},"%"))
po_me_id like concat("%",#{cri.search},"%")
(po_content like concat("%",#{cri.search},"%") or po_title like concat("%",#{cri.search},"%"))
and po_ca_num = #{cri.ca}
and me_id = #{me_id} order by po_num desc limit #{cri.pageStart} , #{cri.perPageNum}

select count(*) from post join member on me_id = po_me_id where
(po_title like concat("%",#{cri.search},"%") or po_content like concat("%",#{cri.search},"%") or po_me_id like concat("%",#{cri.search},"%"))
po_me_id like concat("%",#{cri.search},"%")
(po_content like concat("%",#{cri.search},"%") or po_title like concat("%",#{cri.search},"%"))
po_ca_num = #{cri.ca}
and me_id = #{me_id}

mapper.update:
 		update
 			item
		set
			it_name = #{item.it_name}
		where
			it_num = #{item.it_num}
